mapper.update:
UPDATE product
PROD_NAME = #{prodName} , PROD_DETAIL = #{prodDetail}, MANUFACTURE_DAY = #{manuDate} , PRICE =#{price}, COUNT=#{count}, IMAGE_FILE =#{fileName}
WHERE PROD_NO = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM (SELECT DISTINCT p.PROD_NO,p.PROD_NAME, p.PROD_DETAIL, p.MANUFACTURE_DAY, p.PRICE,p.IMAGE_FILE,p.REG_DATE,p.COUNT,NVL(TRAN_STATUS_CODE,'0') STATUS_CODE FROM PRODUCT p,TRANSACTION t
p.PROD_NO = #{searchKeyword}
p.PROD_NAME LIKE #{searchKeyword}
p.PRICE= #{searchKeyword}
AND p.COUNT != 0
AND p.COUNT = 0
AND p.prod_no IN t.prod_no(+) ORDER BY p.PRICE DESC
AND p.prod_no IN t.prod_no(+) ORDER BY p.PRICE ASC
AND p.prod_no IN t.prod_no(+) ORDER BY p.PROD_NO
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

		SELECT COUNT(*)
		FROM( SELECT DISTINCT p.PROD_NO,p.PROD_NAME
		FROM product p,TRANSACTION t
			
				
					
						p.PROD_NO = #{searchKeyword} 
					
					
						p.PROD_NAME LIKE #{searchKeyword} 
					
					
						p.PRICE= #{searchKeyword} 
					

					
						
							AND p.COUNT != 0
						
						
							AND p.COUNT = 0
						
					

					
						AND p.prod_no IN t.prod_no(+) ORDER BY p.PRICE DESC
					
					
						AND p.prod_no IN t.prod_no(+) ORDER BY p.PRICE ASC
					
					
						AND p.prod_no IN t.prod_no(+) ORDER BY p.PROD_NO
					

				
			
		) countTable
