mapper.update:
UPDATE transaction
PAYMENT_OPTION=#{paymentOption:INTEGER}, RECEIVER_NAME=#{receiverName:VARCHAR}, RECEIVER_PHONE=#{receiverPhone:VARCHAR}, DEMAILADDR=#{divyAddr:VARCHAR}, DLVY_REQUEST=#{divyRequest:VARCHAR}, DLVY_DATE=#{divyDate:DATE}
WHERE tran_No = #{tranNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT TRAN_NO , PROD_NO , BUYER_ID , PAYMENT_OPTION , RECEIVER_NAME, RECEIVER_PHONE, DEMAILADDR, DLVY_REQUEST, TRAN_STATUS_CODE,ORDER_DATA,DLVY_DATE FROM transaction
buyer_id = #{buyerId}
AND TRAN_STATUS_CODE is NULL
AND TRAN_STATUS_CODE in (#{shippingCondition},1)
AND TRAN_STATUS_CODE in (#{shippingCondition},2)
AND TRAN_STATUS_CODE in (#{shippingCondition},3)
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
